import React from 'react';
import styled from 'styled-components';
import Logo from '../img/logo.jpeg';
import login from '../img/loginnn.jpg';


const StyledHeader = styled.header`

    display: flex;
  justify-content: space-between;
  background-color:#5dd8;
box-shadow: inset 0 0 1em rgb(33, 99, 66), 0 0 1em rgb(15, 46, 54);
  align-items: center;
  padding: 20px;
  height: 90px;
  box-shadow: black;

`;

const StyledLogo = styled.img`
    width: 5vw;
    margin: 35px 10px 35px 45px;

`;

const StyledNomeLoja = styled.h2`
    font-family: monospace;
    background-image: linear-gradient(to bottom, rebeccapurple, steelblue, turquoise);




`;

const StyledInput = styled.input`
    width:500px;
    height: 40px;
    border-radius: 10px;
    background-color: pink;
    font-size: 20px;
    text-align: center;
    margin-left: 100px;
`;

const StyledButtonSearch = styled.button`
    width: 30px;
    height: 20%;
    background-color: black;
    border: 0;
    margin-left: -40px;
`;

const StyledLupa = styled.img`
    width: 30px;

`;


 const StyledButtonLogin = styled.button`
   width: 50px;
     height: 50%;
     margin-left: 0px;
 `;

const StyledLogin = styled.img`
    width: 70px;
    padding: 0;

`;


class Header extends React.Component {

    render () {

        return (
            <StyledHeader> <StyledLogo src= {Logo} alt="SpaceToy" />


                <StyledInput placeholder='O que você tá procurando?'/>
                <login isrc="Login"/>
            </StyledHeader>

        )  
    }
}

export default Header;
